import { useRouter } from "next/router";

const Comment = () => {
  const router = useRouter();
  const { id, comment } = router.query;
  return (
    <React.Fragment>
      <h1>Post: {id}</h1>
      <h1>Comment: {comment}</h1>
    </React.Fragment>
  );
};

export default Comment;
